import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AppStateService } from './app-state.service';
import { Post } from 'post.model'; // Import the Post interface

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit{
  title = 'recruitment-task';

  constructor(private http: HttpClient, private appState: AppStateService) {}

  ngOnInit(): void {
    // Check if posts are already in local storage
    const storedPosts = localStorage.getItem('posts');
    if (storedPosts) {
      // If posts are found in local storage, set the app state with them
      this.appState.setPosts(JSON.parse(storedPosts));
    } else {
      // If posts are not found in local storage, fetch from the external API
      this.fetchPosts();
    }
  }

  private fetchPosts(): void {
    // Fetch posts from the external API
    this.http.get<Post[]>('https://jsonplaceholder.typicode.com/posts').subscribe(
      (posts: Post[]) => {
        // Update the app state service with the fetched data
        this.appState.setPosts(posts);
        // Store the fetched posts in local storage
        localStorage.setItem('posts', JSON.stringify(posts));
      },
      (error) => {
        console.error('Failed to fetch posts from the API:', error);
      }
    );
  }
}
