import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { Post } from 'post.model';

interface State {
  externalPosts: any[];
  localPosts: any[];
}

@Injectable({
  providedIn: 'root'
})
export class AppStateService {
  private initialState: State = {
    externalPosts: [],
    localPosts: []

  };

  private stateSubject: BehaviorSubject<State> = new BehaviorSubject<State>(this.initialState);
  state$: Observable<State> = this.stateSubject.asObservable();

  constructor() {}

  setPosts(posts: any[]): void {
    const externalPosts = posts[(posts.length-1)].filter((post:Post) => post.hasOwnProperty('userId'));
    const localPosts = posts.filter(post => !post.hasOwnProperty('userId'));
    console.log(posts);

    this.stateSubject.next({ externalPosts, localPosts });
  }

  setExternalPosts(externalPosts: any[]): void {
    this.stateSubject.next({ ...this.stateSubject.getValue(), externalPosts });
  }

  setLocalPosts(localPosts: any[]): void {
    this.stateSubject.next({ ...this.stateSubject.getValue(), localPosts });
  }

  addExternalPost(post: any): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = [...currentState.externalPosts, post];
    this.stateSubject.next({ ...currentState, externalPosts: updatedPosts });
  }

  addLocalPost(post: any): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = [...currentState.localPosts, post];
    this.stateSubject.next({ ...currentState, localPosts: updatedPosts });
  }

  updateExternalPost(post: any): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = currentState.externalPosts.map(p => (p.id !== post.id ? post : p));
    this.stateSubject.next({ ...currentState, externalPosts: updatedPosts });
  }

  updateLocalPost(post: any): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = currentState.localPosts.map(p => (p.id !== post.id ? post : p));
    this.stateSubject.next({ ...currentState, localPosts: updatedPosts });
  }

  deleteExternalPost(postId: number): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = currentState.externalPosts.filter(p => p.id !== postId);
    this.stateSubject.next({ ...currentState, externalPosts: updatedPosts });
  }

  deleteLocalPost(postId: number): void {
    const currentState = this.stateSubject.getValue();
    const updatedPosts = currentState.localPosts.filter(p => p.id !== postId);
    this.stateSubject.next({ ...currentState, localPosts: updatedPosts });
  }
}
