import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent {

}
<div class="container mt-4 about">
  <h2 class="text-center">About</h2>
  <p class="about-text">
    MyApp is a simple application to showcase Angular components and routing.
    This app allows you to view, create, edit, and delete posts using a REST API.
  </p>
</div>
