import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AppStateService } from './app-state.service';
import { PostsService } from './posts.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'recruitment-task';

  constructor(
    private http: HttpClient,
    private appState: AppStateService,
    private postsService: PostsService
  ) {}

  ngOnInit(): void {
    // Fetch posts from the external API
    this.http.get<any[]>('https://jsonplaceholder.typicode.com/posts').subscribe(
      (externalPosts) => {
        // Save the fetched posts in your local db.json using the postsService
        this.postsService.savePosts(externalPosts).subscribe(
          () => {
            // Fetch the posts from your local db.json using the postsService
            this.postsService.getPosts().subscribe(
              (localPosts) => {
                // Update the app state service with both the external and local posts
                console.log(localPosts);
                this.appState.setExternalPosts(externalPosts);
                this.appState.setLocalPosts(localPosts);
              },
              (error) => {
                console.error('Failed to fetch local posts:', error);
              }
            );
          },
          (error) => {
            console.error('Failed to save external posts:', error);
          }
        );
      },
      (error) => {
        console.error('Failed to fetch external posts:', error);
      }
    );
  }
}
