import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  username = '';
  password = '';
  returnUrl: string;
  error = '';
  private errorMessageHidden = false;

  constructor(private route: ActivatedRoute, private router: Router, private authService: AuthService) {
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';
  }


  ngAfterContentInit(): void {
    if (!this.errorMessageHidden) {
      this.hideErrorMessage();
      this.errorMessageHidden = true;
    }else{this.showErrorMessage();}
  }

  private hideErrorMessage(): void {
    this.error = ''; // Reset the error message to an empty string to hide it.
    const alertMessage = document.querySelector(".alert-message");
    if (alertMessage) {
     alertMessage.classList.add('hidden');
    }
  }
  private showErrorMessage(): void {
    const alertMessage = document.querySelector(".alert-message");
    if (alertMessage) {
      alertMessage.classList.add('show');
      alertMessage.classList.remove("hidden");
    }
  }

  login(): void {
    this.authService.login(this.username, this.password).subscribe(
      () => {
        this.router.navigateByUrl(this.returnUrl);
      },
      error => {
        this.error = error.error.message;
      }
    );
  }

}


<div class="container">
  <h1>Login</h1>
  <form (submit)="login();">
    <div class="form-group">
      <label for="username">Username:</label>
      <input type="text" [(ngModel)]="username" name="username" id="username" class="form-control" required />
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" [(ngModel)]="password" name="password" id="password" class="form-control" required />
    </div>
    <div class="d-grid gap-2">
    <button type="submit" class="btn btn-primary btn-block">Login</button>
  </div>
    <div class="alert alert-danger mt-3 alert-message">{{ error }}</div>
  </form>
</div>
