import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent {

}
<div class="container mt-4 about">
  <h2 class="text-center">About</h2>
  <p class="about-text">
    waab is a simple application to showcase my Angular skills in components, routing, services...
    This app allows you to view, create, edit, and delete posts using a REST API. <br>
  <a href="https://www.linkedin.com/in/waab/" target="_blank">WaaB</a>
  </p>
</div>
