import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AppStateService } from './app-state.service';
import { PostsService } from './posts.service';
import { merge } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'recruitment-task';

  constructor(
    private http: HttpClient,
    private appState: AppStateService,
    private postsService: PostsService
  ) {

        // Fetch posts from both the external API and the local server created
        const externalPosts$ = this.http.get('https://jsonplaceholder.typicode.com/posts');
        const localPosts$ = this.postsService.getPosts();

        // Merge the results from both observables
        merge(externalPosts$, localPosts$).subscribe(
          (posts: any[]) => {
            // Separate the fetched posts from the external API
            // and the posts from the local server
            const externalPosts = posts.filter(post => post.hasOwnProperty('userId'));
            const localPosts = posts.filter(post => !post.hasOwnProperty('userId'));
             this.prints('1'+externalPosts);  

            // Update the app state service with the fetched data
            this.appState.setExternalPosts(externalPosts); // Create a method in appState service to set external posts
            this.appState.setLocalPosts(localPosts); // Create a method in appState service to set local posts
          },
          (error) => {
            console.error('Failed to fetch posts:', error);
          }
        );
  }

  ngOnInit(): void {
  }

  prints(pts:any): void
  {
    console.log(pts);
  };
}
